USE LojaLMS;

CREATE TABLE Produtos (
    ProdutoID INT AUTO_INCREMENT PRIMARY KEY,   -- Identificador único para cada produto
    Nome VARCHAR(50),                           -- Nome do produto
    Categoria VARCHAR(50),                      -- Categoria do produto
    Preco DECIMAL(10, 2),                       -- Preço do produto
    Estoque INT                                 -- Quantidade em estoque
    );
    
-- Criação da tabela Clientes
CREATE TABLE Clientes (
    ClienteID INT AUTO_INCREMENT PRIMARY KEY,   -- Identificador único para cada cliente
    Nome VARCHAR(100),                          -- Nome do cliente
    Email VARCHAR(100),                         -- Email do cliente
    Telefone VARCHAR(20)                        -- Telefone do cliente
);

-- Criação da tabela Vendas
CREATE TABLE Vendas (
    VendaID INT AUTO_INCREMENT PRIMARY KEY,     -- Identificador único para cada venda
    ClienteID INT,                              -- Referência ao cliente
    DataVenda DATETIME DEFAULT CURRENT_TIMESTAMP,  -- Data e hora da venda
    Total DECIMAL(10, 2),                       -- Valor total da venda
    FOREIGN KEY (ClienteID) REFERENCES Clientes(ClienteID)  -- Chave estrangeira
);

-- Criação da tabela ItensVendidos
CREATE TABLE ItensVendidos (
    ItemID INT AUTO_INCREMENT PRIMARY KEY,      -- Identificador único para cada item vendido
    VendaID INT,                                -- Referência à venda
    ProdutoID INT,                              -- Referência ao produto
    Quantidade INT,                             -- Quantidade vendida
    PrecoUnitario DECIMAL(10, 2),               -- Preço unitário do produto
    FOREIGN KEY (VendaID) REFERENCES Vendas(VendaID),     -- Chave estrangeira
    FOREIGN KEY (ProdutoID) REFERENCES Produtos(ProdutoID) -- Chave estrangeira
);







